mapper.update:
UPDATE group_buyings
SET remain = remain - 1
WHERE id = #{groupBuyingId}
      AND start_time <= #{buyingTime}
      AND group_buyings.end_time >= #{buyingTime}
      AND group_buyings.remain > 0
      AND group_buyings.examine = TRUE
      AND group_buyings.publish = TRUE;

mapper.select:
SELECT *
FROM group_buyings
WHERE examine = TRUE AND publish = TRUE AND group_buyings.end_time <= now()
ORDER BY created_at DESC
LIMIT #{limit}
OFFSET #{offset}
